package lec1;

public class max_3_num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c;
		a=89;
		b=38;
		c=9;
		if(a>=b && a>=c) {
			System.out.println(" a is max");
		}
		else {
			if(b>=c) {
				System.out.println("b is max");
			}
			else {
				System.out.println("c is maximum");
			}
		}

	}

}
